// 1. Access the input items. In N8N, 'items' is the standard array of input objects.
// If the input is a single array inside one item (like the file upload), we flatten it.
const inputData = items[0].json instanceof Array ? items[0].json : items;

const normalizeCISA = inputData.map(item => {
  // Handle cases where the data might be wrapped or direct
  const advisory = item.json || item;
  const rawMarkdown = advisory.markdown || "";
  const url = advisory.url || "";

  // --- Step A: Date Extraction (From URL) ---
  // CISA URLs structure: .../alerts/YYYY/MM/DD/title
  // We extract this to use as the "published_at" date.
  const dateMatch = url.match(/\/(\d{4})\/(\d{2})\/(\d{2})\//);
  let publishedAt = new Date().toISOString(); // Default to now if not found

  if (dateMatch) {
    // Construct ISO string from URL parts (YYYY-MM-DD)
    publishedAt = new Date(`${dateMatch[1]}-${dateMatch[2]}-${dateMatch[3]}`).toISOString();
  }

  // --- Step B: Clear (Lowercase) ---
  let cleanText = rawMarkdown.toLowerCase();

  // --- Step C: Normalization (Boilerplate Removal) ---

  // 1. Remove standard CISA Header/Footer noise
  // We use specific phrases found in your JSON to strip the "Official website" banner and footer links
  const boilerPlatePhrases = [
    "an official website of the united states government",
    "official websites use .gov",
    "secure .gov websites use https",
    "a .gov website belongs to an official government organization",
    "share sensitive information only on official, secure websites",
    "lock a locked padlock",
    "cisa central 1-844-say-cisa",
    "contact@cisa.dhs.gov",
    "website feedback",
    "about cisa",
    "skip to main content" // common web scraper artifact
  ];

  boilerPlatePhrases.forEach(phrase => {
    cleanText = cleanText.split(phrase).join(' ');
  });

  // 2. Remove Markdown Links but keep text: [Text](URL) -> Text
  // This simplifies the text for vector embedding
  cleanText = cleanText.replace(/\[([^\]]+)\]\([^\)]+\)/g, '$1');

  // 3. Remove URLs that are left over (http/https)
  cleanText = cleanText.replace(/https?:\/\/\S+/g, '');

  // 4. Remove special characters (keep alphanumeric, standard punctuation for context)
  cleanText = cleanText.replace(/[^a-z0-9\s.,\-_]/g, '');

  // 5. Collapse multiple spaces/newlines into single space
  cleanText = cleanText.replace(/\s+/g, ' ').trim();

  // --- Step D: Construct Output ---
  return {
    json: {
      source: "cisa_advisory",
      // Generate a simple ID from the URL slug if standard ID is missing
      id: url.split('/').pop() || "unknown_id", 
      url: url,
      author: "CISA", // Static author for this source

      // Content for Vector Store
      raw_text: rawMarkdown, // Keep original for reference if needed
      normalized_text: cleanText, // The clean, semantic data

      // Timestamps
      published_at: publishedAt,
      processed_at: new Date().toISOString()
    }
  };
});

return normalizeCISA;

/**
 * PREPROCESAMIENTO FINAL ‚Äî COMPATIBLE CON ESTRUCTURA N8N
 *
 * N8N ya entrega cada elemento del array original de CISA
 * como un item individual del formato:
 *
 *   items = [ {json:{...}}, {json:{...}}, ... ]
 *
 * Por lo tanto, este script SOLO debe:
 *  - Iterar sobre items[]
 *  - Normalizar cada registro
 *  - Devolver un item por cada entrada
 */

function htmlToText(html) {
    if (!html) return "";
    return String(html)
        .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, ' ')
        .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, ' ')
        .replace(/<\/?(br|p|div|li|ul|h\d)[^>]*>/gi, '\n')
        .replace(/<[^>]+>/g, ' ')
        .replace(/\s{2,}/g, ' ')
        .trim();
}

let output = [];

// Procesar cada item entrante
for (const item of items) {
    const r = item.json;

    let content = "";

    // Normalizaci√≥n de contenido
    if (r.markdown)      content = String(r.markdown);
    else if (r.text)     content = String(r.text);
    else if (r.body)     content = String(r.body);
    else if (r.html)     content = htmlToText(r.html);
    else if (r.raw)      content = String(r.raw);
    else                 content = "";

    output.push({
        json: {
            url: r.url || r.link || null,
            content,
            success: r.success === true,
            original_record: r
        }
    });
}

return output;

/**
 * SCRIPT FINAL CISA ‚Äî CURADO COMPLETO (CON CTI + TTPs MITRE)
 *
 * Entrada esperada (items[]): cada item.json debe venir del preprocesador y tener:
 *   { url: "...", content: "markdown completo extra√≠do", success: true, ... }
 *
 * Salida: un item por cada advisory con la estructura unificada:
 *   {
 *     id, source, source_url,
 *     ingest_timestamp, published_timestamp,
 *     title, text, summary,
 *     cves, indicators, ttps, tags,
 *     author, references, raw, provenance
 *   }
 *
 * Cambios clave incorporados:
 * - extracci√≥n SOLO del contenido relevante del advisory (quita header/footer/menu)
 * - extracci√≥n de CVEs, TTPs ATT&CK, IoCs (IPs, urls, domains, emails, hashes)
 * - filtros: eliminar urls/emails de cisa.gov, mitre/nvd/nist/cve.org
 * - nuevo indicator "cti" (confidence 100) si el t√≠tulo contiene ransom/apt/threat
 * - generaci√≥n autom√°tica de TTPs MITRE seg√∫n tipo CTI (ransomware / apt / threat)
 * - escala de confidence actualizada (cti>cve>...)
 * - indicador "informational" (confidence baja) cuando no hay IoCs t√©cnicos o advisory guidance
 *
 * Comentarios detallados sobre timestamps y confidence est√°n incluidos en el bloque correspondiente.
 */


/* ============================
   UTILIDADES B√ÅSICAS
   ============================ */

function uuid() {
  // Genera un id simple √∫nico (no criptogr√°fico)
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

/* Normaliza una fecha de texto a ISO. Si no puede parsear, devuelve fallback (string ISO). */
function normalizeTimestamp(raw, fallback) {
  if (!raw || typeof raw !== 'string') return fallback;
  const p = Date.parse(raw);
  if (!isNaN(p)) return new Date(p).toISOString();
  return fallback;
}

/* Devuelve hora actual en ISO (UTC). Usamos UTC para consistencia entre nodos/entornos. */
function nowIso() {
  return (new Date()).toISOString();
}


/* ============================
   EXTRACCI√ìN DEL CONTENIDO RELEVANTE
   ============================ */

/**
 * extractRelevantText(markdown)
 * - Toma el markdown completo y devuelve SOLO el cuerpo relevante del advisory.
 * - Estrategia:
 *   1) Buscar el t√≠tulo H1 (# ...) y recortar todo lo anterior.
 *   2) Cortar secciones finales comunes (Please share..., Related Advisories, footer).
 *   3) Reemplazar links markdown [txt](url) -> txt.
 *   4) Limpiar markdown b√°sico (negrillas/italicas y espacios repetidos).
 */
function extractRelevantText(markdown) {
  if (!markdown) return '';

  let text = String(markdown);

  // 1) arrancar desde H1 si existe
  const titleMatch = text.match(/^#\s+.+/m);
  if (titleMatch) {
    const idx = text.indexOf(titleMatch[0]);
    if (idx >= 0) text = text.slice(idx);
  }

  // 2) cortar secciones institucionales comunes al final
  const hardStops = [
    '## Please share your thoughts',
    '## Related Advisories',
    'Cybersecurity & Infrastructure Security Agency',
    'An official website of the U.S. Department of Homeland Security',
    'Website Feedback',
    'About CISA'
  ];
  for (const stop of hardStops) {
    const i = text.indexOf(stop);
    if (i !== -1) text = text.slice(0, i);
  }

  // 3) reemplazar links markdown por texto visible
  text = text.replace(/\[([^\]]+)\]\([^)]+\)/g, '$1');

  // 4) limpieza b√°sica
  text = text
    .replace(/\*\*/g, '')
    .replace(/_/g, '')
    .replace(/\r\n/g, '\n')
    .replace(/\n{3,}/g, '\n\n')
    .replace(/[ \t]{2,}/g, ' ')
    .trim();

  return text;
}


/* ============================
   REGEX / EXTRACTORS
   ============================ */

const cveRegex    = /\bCVE-\d{4}-\d{4,7}\b/gi;
const ttpRegex    = /\bT\d{4}(?:\.\d{3})?\b/g;
const ipRegex     = /\b(?:(?:25[0-5]|2[0-4]\d|1?\d{1,2})\.){3}(?:25[0-5]|2[0-4]\d|1?\d{1,2})\b/g;
const emailRegex  = /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}\b/g;
const urlRegex    = /\bhttps?:\/\/[^\s"')]+/gi;
const domainRegex = /\b([a-zA-Z0-9-]+\.)+(com|org|net|gov|edu|io|info|biz|us|uk)\b/gi;
const hashRegex   = /\b[a-f0-9]{32}\b|\b[a-f0-9]{40}\b|\b[a-f0-9]{64}\b/gi;

/* devuelve array √∫nico */
function extractUnique(regex, text) {
  if (!text) return [];
  const m = text.match(regex) || [];
  return Array.from(new Set(m));
}

function extractCVEs(text) {
  return extractUnique(cveRegex, text).map(c => c.toUpperCase());
}
function extractTTPs(text) {
  return extractUnique(ttpRegex, text);
}
function extractIndicatorsRaw(text) {
  return {
    ips:    extractUnique(ipRegex, text),
    urls:   extractUnique(urlRegex, text),
    domains: extractUnique(domainRegex, text).map(s => s.toLowerCase()),
    emails: extractUnique(emailRegex, text),
    hashes: extractUnique(hashRegex, text)
  };
}


/* ============================
   LISTAS NEGRAS (CISA, CVE/MITRE/NIST)
   ============================ */

const cisaDomainsBlacklist = [
  'cisa.gov', 'www.cisa.gov'
];
const cisaEmailsBlacklist = [
  '@cisa.gov'
];
const cveDomainsBlacklist = [
  'cve.org', 'nvd.nist.gov', 'nist.gov', 'mitre.org'
];
const cveEmailsBlacklist = [
  '@mitre.org', '@nist.gov'
];

/* filtro de url: cualquier url que contenga cisa.gov o dominios CVE oficiales */
function isBlacklistedUrl(url) {
  if (!url) return false;
  const u = String(url).toLowerCase();
  if (u.includes('cisa.gov')) return true;
  for (const d of cveDomainsBlacklist) if (u.includes(d)) return true;
  return false;
}
function isBlacklistedEmail(email) {
  if (!email) return false;
  const e = String(email).toLowerCase();
  for (const s of cisaEmailsBlacklist) if (e.endsWith(s)) return true;
  for (const s of cveEmailsBlacklist) if (e.endsWith(s)) return true;
  return false;
}


/* ============================
   CONFIDENCE (ESCALA RE-ESCALEADA)
   ============================
   Nueva prioridad: CTI (max) > CVE > hash > ip > domain > url > email > informational
   - CTI: 100
   - CVE: 95
   - hash:90
   - ip:85
   - domain:80
   - url:70
   - email:50
   - informational:10
   - default:30
*/
function confidence(type) {
  switch ((type||'').toLowerCase()) {
    case 'cti': return 100;
    case 'cve': return 95;
    case 'hash': return 90;
    case 'ip': return 85;
    case 'domain': return 80;
    case 'url': return 70;
    case 'email': return 50;
    case 'informational': return 10;
    default: return 30;
  }
}

/* ============================
   DETECCI√ìN DE CTI Y GENERACI√ìN DE TTPs MITRE
   ============================ */

/* determina si el advisory es CTI por t√≠tulo */
function isCTIAdvisory(title) {
  if (!title) return false;
  const t = title.toLowerCase();
  return t.includes('ransomware') || t.includes('apt') || t.includes('threat');
}

/* funci√≥n que mapea t√≠tulos CTI a TTPs MITRE relevantes */
function generateCTITTPs(title) {
  if (!title) return [];
  const t = title.toLowerCase();
  if (t.includes('ransomware')) {
    return [
      'T1486', // Data Encrypted for Impact
      'T1059', // Command and Scripting Interpreter
      'T1569', // System Services (Service Execution)
      'T1027', // Obfuscated Files or Information
      'T1041', // Exfiltration Over C2
      'T1078', // Valid Accounts
      'T1047'  // Windows Management Instrumentation
    ];
  }
  if (t.includes('apt')) {
    return [
      'T1059', // Command Execution
      'T1071', // Application Layer Protocol
      'T1105', // Ingress Tool Transfer
      'T1082', // System Information Discovery
      'T1566', // Phishing (Spearphishing)
      'T1003', // Credential Dumping
      'T1041'  // Exfiltration Over C2
    ];
  }
  if (t.includes('threat')) {
    return [
      'T1595', // Active Scanning
      'T1592', // Gather Victim Identity Info
      'T1608', // Stage Capabilities
      'T1598', // Phishing for Information
      'T1057', // Process Discovery
      'T1018'  // Remote System Discovery
    ];
  }
  return [];
}

/* crea indicador CTI */
function makeCTIIndicator() {
  return {
    type: 'cti',
    value: 'threat-intel',
    confidence: confidence('cti')
  };
}

/* ============================
   'informational' helper
   ============================ */
function isInformationalAdvisoryTitle(title) {
  if (!title) return false;
  const t = title.toLowerCase();
  const keywords = ['guidance', 'control', 'controls', 'best practice', 'best practices', 'mitigation', 'checklist', 'recommendation', 'recommendations', 'guidelines', 'overview'];
  return keywords.some(k => t.includes(k));
}
function makeInformationalIndicator() {
  return { type: 'informational', value: 'context', confidence: confidence('informational') };
}

/* ============================
   AUTO TAGS (heur√≠sticos)
   ============================ */
function autoTags(text) {
  if (!text) return ['osint'];
  const t = text.toLowerCase();
  const tags = new Set(['osint', 'threat']);
  if (t.includes('vulnerability') || t.includes('cve')) tags.add('vulnerability');
  if (t.includes('exploit')) tags.add('exploit');
  if (t.includes('ransomware')) tags.add('ransomware');
  if (t.includes('malware')) tags.add('malware');
  if (t.includes('phishing')) tags.add('phishing');
  if (t.includes('ics')) tags.add('ics');
  if (t.includes('known exploited') || t.includes('kev')) tags.add('kev');
  if (t.includes('patch') || t.includes('update')) tags.add('patching');
  return Array.from(tags);
}


/* ============================
   PROCESAMIENTO PRINCIPAL (uno-a-uno)
   ============================ */

const output = [];

for (const item of items) {
  // item.json viene del preprocesador
  const rec = item.json || {};
  const rawOriginal = rec.content || '';       // guardamos el markdown completo en raw.content
  const raw = extractRelevantText(rawOriginal); // texto reducido y relevante (usar para extracci√≥n)
  const source_url = rec.url || null;

  // TITULO extra√≠do del H1 (si existe) o del slug de la URL
  let title = null;
  const titleMatch = raw.match(/^#\s*(.+)/m);
  if (titleMatch) title = titleMatch[1].trim();
  else if (source_url) {
    const slug = source_url.split('/').filter(Boolean).pop() || null;
    title = slug ? slug.replace(/[-_]+/g, ' ') : null;
  }

  // TIMESTAMPS
  // ingest_timestamp: momento en que el curador ingiere el registro (ISO UTC)
  const ingest_timestamp = nowIso();

  // published_timestamp: intentamos parsear alguna fecha en el texto (no siempre presente)
  // Estrategia: no hay heur√≠stica de "Release Date" adicional aqu√≠ por simplicidad; fallback = ingest_timestamp.
  // Si quieres, podemos a√±adir heur√≠sticos m√°s complejos para extraer fechas.
  let published_timestamp = ingest_timestamp; // fallback
  // (opcional) intentar detectar fechas en texto - simple regex (YYYY-MM-DD)
  const isoDateMatch = raw.match(/\b(20\d{2}-\d{2}-\d{2})\b/);
  if (isoDateMatch) {
    published_timestamp = normalizeTimestamp(isoDateMatch[1], ingest_timestamp);
  }

  // EXTRAER CVEs, TTPs, INDICADORES (raw)
  const cves = extractCVEs(raw);               // CVEs canonizados
  let ttps = extractTTPs(raw);                 // TTPs expl√≠citos en texto
  const indicatorsRaw = extractIndicatorsRaw(raw);

  // Construir indicadores a partir de lo extra√≠do (antes de filtrar)
  let indicators = [];

  (indicatorsRaw.ips || []).forEach(ip => indicators.push({ type: 'ip', value: ip, confidence: confidence('ip') }));
  (indicatorsRaw.urls || []).forEach(u => indicators.push({ type: 'url', value: u, confidence: confidence('url') }));
  (indicatorsRaw.domains || []).forEach(d => indicators.push({ type: 'domain', value: d, confidence: confidence('domain') }));
  (indicatorsRaw.emails || []).forEach(e => indicators.push({ type: 'email', value: e, confidence: confidence('email') }));
  (indicatorsRaw.hashes || []).forEach(h => indicators.push({ type: 'hash', value: h, confidence: confidence('hash') }));
  (cves || []).forEach(c => indicators.push({ type: 'cve', value: c, confidence: confidence('cve') }));

  // FILTRADO: eliminar URLs/Domains/Emails que sean feedback / institucionales de CISA o referencias CVE oficiales
  indicators = indicators.filter(ind => {
    if (!ind || !ind.type || !ind.value) return false;
    const val = String(ind.value).toLowerCase();

    // URLs y dominios: eliminar si contienen cisa.gov o dominios CVE oficiales
    if (ind.type === 'url' || ind.type === 'domain') {
      if (val.includes('cisa.gov')) return false;
      for (const d of cveDomainsBlacklist) if (val.includes(d)) return false;
    }

    // Emails: eliminar si terminan con @cisa.gov o dominios CVE relacionados
    if (ind.type === 'email') {
      for (const s of cisaEmailsBlacklist) if (val.endsWith(s)) return false;
      for (const s of cveEmailsBlacklist) if (val.endsWith(s)) return false;
    }

    return true;
  });

  // DETECCION CTI (por t√≠tulo) -> si true, inyectar indicador CTI y generar TTPs MITRE
  const isCTI = isCTIAdvisory(title || '');

  if (isCTI) {
    // agregar indicador CTI (confidence 100)
    indicators.push(makeCTIIndicator());

    // generar TTPs MITRE asociados al tipo (ransomware | apt | threat)
    const generated = generateCTITTPs(title || '');
    if (Array.isArray(generated) && generated.length > 0) {
      // a√±adir TTPs generados si no est√°n ya presentes
      for (const t of generated) if (!ttps.includes(t)) ttps.push(t);
    }
  }

  // L√≥gica para "informational" si NO hay indicadores t√©cnicos (ip/domain/url/hash/cve/email)
  const hasTechnicalIndicators = indicators.some(ind => ['ip','domain','url','hash','cve','email'].includes((ind.type||'').toLowerCase()));

  // Si no es CTI (CTI tiene prioridad), y no hay indicadores t√©cnicos o el t√≠tulo indica guidance -> informational
  if (!isCTI) {
    if (!hasTechnicalIndicators || isInformationalAdvisoryTitle(title || '')) {
      indicators.push(makeInformationalIndicator());
    }
  }

  // (Opcional) si existen indicadores duplicados, eliminar duplicados por type+value
  const dedup = {};
  indicators = indicators.filter(ind => {
    const k = (ind.type||'') + '::' + (String(ind.value||''));
    if (dedup[k]) return false;
    dedup[k] = true;
    return true;
  });

  // TITLE final (si no exist√≠a anteriormente)
  let finalTitle = title;
  if (!finalTitle && source_url) {
    finalTitle = source_url.split('/').filter(Boolean).pop() || null;
  }

  // REFERENCES: tomar urls no filtradas encontradas en rawOriginal (pueden ser √∫tiles)
  const allUrls = extractUnique(urlRegex, rawOriginal);
  const references = (allUrls || []).filter(u => !isBlacklistedUrl(u));

  // SUMMARY: primer p√°rrafo o primeros 240 caracteres
  let summary = null;
  if (raw) {
    const firstPara = raw.split(/\n{1,}/).find(p => p.trim().length > 0) || raw;
    summary = firstPara.length > 240 ? firstPara.slice(0,237).trim() + '...' : firstPara.trim();
  }

  // ENSAMBLAR OBJETO UNIFICADO
  const unified = {
    id: 'cisa::' + uuid(),
    source: 'cisa',
    source_url: source_url,

    // ingest_timestamp: aplicamos hora local del sistema en ISO (UTC used here)
    ingest_timestamp: ingest_timestamp,

    // published_timestamp: si no puede derivarse se deja igual al ingest_timestamp (fallback)
    published_timestamp: published_timestamp || ingest_timestamp,

    title: finalTitle || null,
    text: raw || '',
    summary: summary || null,

    cves: cves || [],

    indicators: indicators || [],

    ttps: ttps || [],

    tags: autoTags(raw),

    author: {
      name: 'Cybersecurity and Infrastructure Security Agency',
      id: 'CISA',
      type: 'agency',
      profile_url: 'https://www.cisa.gov/'
    },

    references: references || [],

    raw: {
      content: rawOriginal   // preservamos el markdown original sin recortar
    },

    provenance: {
      ingested_from_file: 'CISA_Markdown.json',
      original_record_id: source_url || null
    }
  };

  output.push({ json: unified });
}

/* Si por alguna raz√≥n no se proces√≥ nada, devolvemos un warning ITEM para no romper n8n */
if (output.length === 0) {
  return [{ json: { warning: 'No records processed by CISA curador' } }];
}

return output;

// ============================================================================
// SCRIPT FINAL ‚Äî CURADO TWITTER (N8N READY)
// Alineado con el schema unificado para RAG y el script CISA
// ============================================================================

// Helpers
function normalizeTimestamp(ts) {
  if (!ts) return new Date().toISOString();
  const parsed = new Date(ts);
  return isNaN(parsed.getTime()) ? new Date().toISOString() : parsed.toISOString();
}

// Extract CVEs
function extractCVEs(text) {
  const regex = /\bCVE-\d{4}-\d{4,7}\b/gi;
  return text.match(regex) || [];
}

// Extract IOC (IPs, domains, urls, hashes, emails)
function extractIndicators(text) {
  const indicators = [];

  const urlRegex = /\bhttps?:\/\/[^\s)]+/gi;
  const ipRegex = /\b\d{1,3}(\.\d{1,3}){3}\b/g;
  const emailRegex = /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}\b/g;
  const hashRegex = /\b[A-Fa-f0-9]{32,64}\b/g;

  const urls = text.match(urlRegex) || [];
  const ips = text.match(ipRegex) || [];
  const emails = text.match(emailRegex) || [];
  const hashes = text.match(hashRegex) || [];

  // Filtering noisy URLs: remove twitter internal tracking, t.co, media, and CISA if present
  const blacklistPatterns = [
    "https://t.co/",
    "https://x.com/",
    "twitter.com",
    "pbs.twimg.com",
    "cisa.gov",            // internal to CISA, not an IOC
  ];

  urls.forEach(u => {
    if (!blacklistPatterns.some(b => u.includes(b))) {
      indicators.push({ type: "url", value: u });
    }
  });

  ips.forEach(ip => indicators.push({ type: "ip", value: ip }));
  emails.forEach(e => {
    // Filter CISA internal emails
    if (!e.endsWith("@cisa.gov")) {
      indicators.push({ type: "email", value: e });
    }
  });
  hashes.forEach(h => indicators.push({ type: "hash", value: h }));

  return indicators;
}

// Confidence scoring
function assignConfidence(indType, cves, isCTI) {
  if (isCTI) return 100; // Highest
  if (indType === "cve" && cves.length > 0) return 90;
  if (["ip", "domain", "hash", "url", "email"].includes(indType)) return 70;
  return 30; // informational
}

// MITRE ATT&CK TTP generator
function generateTTP(text, isCTI) {
  if (!isCTI) return [];

  const ttp = [];

  const map = [
    { pattern: /ransomware/i, t: "TA0040" },
    { pattern: /apt/i, t: "TA0010" },
    { pattern: /lateral/i, t: "T1021" },
    { pattern: /persistence/i, t: "T1053" },
    { pattern: /credential/i, t: "T1555" },
    { pattern: /c2|command and control/i, t: "T1071" },
    { pattern: /exfiltration/i, t: "T1041" }
  ];

  for (const entry of map) {
    if (entry.pattern.test(text)) ttp.push(entry.t);
  }

  if (ttp.length === 0) ttp.push("TA0001"); // default CTI baseline

  return ttp;
}

